import React, { useEffect, useState } from 'react';

export const Listar = () => {

    const [data, setData] = useState([]);

    const listarProdutos = async  => {
        var valores = [
            {
                "id": 2,
                "mnome":  "Teclado",
                "valor": 120.50,
                "quantidade": 35,               
            },
            {                
                "id": 1,
                "mnome":  "Mouse",
                "valor": 35.50,
                "quantidade": 45  
            }
        ]

        setData(valores);
    }

    useEffect(() => {
        listarProdutos();
    }, []);
    return(
        <>
        <h1>Listar</h1>

        <table>
            <thead>
                <tr>
                   <td>ID</td>
                   <td>Nome</td>
                   <td>Valor</td>
                   <td>Quantidade</td>
                   <td>Ações</td>
                </tr>
                <tbody>
                    {data.map(produto =>(
                        <tr>
                            <td>{produto.id}</td>
                            <td>{produto.mnome}</td>
                            <td>{produto.valor}</td>
                            <td>{produto.quantidade}</td>
                            <td>Visualizer Editar Apagar</td>
                        </tr>
                    ))}            
                </tbody>
            </thead>
        </table>
        </>
    );
}
