import styled from "styled-components";

export const NavList = styled.ul`
    display: flex;
    background-color: #0d6efd;
    padding: 15px 5px;
    font-family: sans-serif;
    margin-bottom: 15px;
    a{
        text-decoration: none;
        color: #f9f9f9;
        margin: 0 8px;
    }
    a:hover{
        color: #d0d2db;
    }
    li{
        font-size: 1.0rem;
        list-style: none;
    }
`;

export const ButtonInfo = styled.button`  
    padding: 5px 8px;
    
    
    font-size: 15px;
    :hover{
        background-color: #0dcaf0;
        color: #fff;
    }
`;
