import React, { useEffect, useState } from 'react';
import { Menu } from '../../components/Menu';

export const Listar = () => {

    const [data, setData] = useState([]);

    const listarProdutos = async  => {
        var valores = [
            {
                "id": 3,
                "mnome":  "Pen Drive",
                "valor": 150.50,
                "quantidade": 20,               
            },
            {
                "id": 2,
                "mnome":  "Teclado",
                "valor": 120.50,
                "quantidade": 35,               
            },
            {                
                "id": 1,
                "mnome":  "Mouse",
                "valor": 35.50,
                "quantidade": 45  
            }
        ]

        setData(valores);
    }

    useEffect(() => {
        listarProdutos();
    }, []);
    return(
        <>
        <Menu />
        <h1>Listar</h1>

        <table>
            <thead>
                <tr>
                   <td>ID</td>
                   <td>Nome</td>
                   <td>Valor</td>
                   <td>Quantidade</td>
                   <td>Ações</td>
                </tr>
                <tbody>
                    {data.map(produto =>(
                        <tr key={produto.id}>
                            <td>{produto.id}</td>
                            <td>{produto.mnome}</td>
                            <td>{produto.valor}</td>
                            <td>{produto.quantidade}</td>
                            <td>Visualizer Editar Apagar</td>
                        </tr>
                    ))}            
                </tbody>
            </thead>
        </table>
        </>
    );
}
