import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Menu } from '../../components/Menu';
import { Container, ContudoTitulo, Titulo, BotaoAcao, ButtomSuccess, Table } from '../../styles/custom_adm';

export const Listar = () => {

    const { state } = useLocation();

    const [data, setData] = useState([]);

    const [status] = useState({
        type: state? state.type : "",
        mensagem: state? state.mensagem : ""
    });

    const listarProdutos = async  => {
        var valores = [
            {
                "id": 3,
                "mnome":  "Pen Drive",
                "valor": 150.50,
                "quantidade": 20,               
            },
            {
                "id": 2,
                "mnome":  "Teclado",
                "valor": 120.50,
                "quantidade": 35,               
            },
            {                
                "id": 1,
                "mnome":  "Mouse",
                "valor": 35.50,
                "quantidade": 45  
            }
        ]

        setData(valores);
    }

    useEffect(() => {
        listarProdutos();
    }, []);

    const apagarProduto = async (idProduto) => {
        console.log(idProduto);
    }

    return(
        <Container>
        <Menu />
        <ContudoTitulo>
            <Titulo>Listar</Titulo>   
            <BotaoAcao>
            <Link to="/cadastrar"><ButtomSuccess type="button">Cadastrar</ButtomSuccess></Link>
            </BotaoAcao>                         
        </ContudoTitulo>        

        {status.type === "success" ? <p style={{color: "green"}}>{status.mensagem}</p> : ""}


        

        <Table>
            <thead>
                <tr>
                   <th>ID</th>
                   <th>Nome</th>
                   <th>Valor</th>
                   <th>Quantidade</th>
                   <th>Ações</th>
                </tr>
                <tbody>
                    {data.map(produto =>(
                        <tr key={produto.id}>
                            <td>{produto.id}</td>
                            <td>{produto.nome}</td>
                            <td>{produto.valor}</td>
                            <td>{produto.quantidade}</td>
                            <td>
                                <Link to={"/visualizar/" + produto.id}><button type="button">Visualizar</button></Link>{" "}
                              
                                <Link to={"/editar/" + produto.id}><button type="button">Editar</button></Link>{" "}
                              <Link to ={"#"}><button onClick={() => apagarProduto(produto.id)}>Apagar</button></Link>
                                </td>
                                
                        </tr>
                    ))}            
                </tbody>
            </thead>
        </Table>
        </Container>
    );
}
